import { DN800, N800 } from '@atlaskit/theme/colors';
var headingColor = {
  light: N800,
  dark: DN800
};
export var headingStyle = {
  display: 'flex',
  padding: '0 0 13px 0',
  fontWeight: 'bold',
  color: N800
};
export var monthAndYearStyle = function monthAndYearStyle(mode) {
  return {
    flexBasis: '100%',
    textAlign: 'center',
    color: headingColor[mode]
  };
};
export var arrowLeftButtonStyle = {
  marginLeft: 8
};
