import * as React from "react";
import { useDispatch } from "react-redux";
import { Dispatch } from "redux";
import RestaurantAction from "./../../data/action";
import { Controller } from "react-hook-form";

import { IRestaurant } from "../../typing/data";
import {
	DatePicker,
	DateTimePicker,
	TimePicker,
} from "@atlaskit/datetime-picker";
import {
	ALlDetail,
	TextDetail,
	Button,
	PlainText,
} from "../../container/style";

interface IReservation {
	data: IRestaurant;
	changePage: (action:string) => void;
	// submitReserve:()=>void
}
const Reservation: React.FC<IReservation> = (props) => {
	const dispatch = useDispatch<Dispatch<RestaurantAction.TRestaurantReduce>>();
	console.log("data", props.data);
	const onChange = (value: unknown) => {
		console.log(value);
	};

	return (
		<>
				<p className="text-center">{props.data.name}</p>
				<TextDetail>
					<p>Name</p>
					<p>Darunporn</p>
				</TextDetail>
				<TextDetail>
					<p>How people to book</p>
					<p>
						<input
							type="text"
							onChange={(e)=>{								
								dispatch({
									type:'INPUT_PEOPLE',
									payload: e.target.value
								})
							}}
						/>
					</p>
				</TextDetail>
				<TextDetail>
					<p>Free Queue</p>
					<p>{props.data.totalQueue}</p>
				</TextDetail>
				<TextDetail>
					<div>Time</div>
					<div style={{ width: "80%" }}>

								<DateTimePicker
									id="datetimepicker-1"
									onChange={(e)=>{
										dispatch({
											type:"INPUT_DATETIME",
											payload:e
										})
									}}
									// testId={"dateTimePicker"}
									/>

					</div>
				</TextDetail>

				<TextDetail className="mt-3">
					<button
						onClick={(e) =>
							dispatch({
								type: "MODAL_RESERVATION",
								payload: false,
							})
						}>
						Cancel
					</button>
					<button onClick={(e=>props.changePage("next"))} >Reserve</button>
				</TextDetail>
		</>
	);
};
export default Reservation;
