import { createStore } from "redux";
import RestaurantType from "../typing/data";
import RestaurantAction from "./action";
import _ from "lodash";
export const initialState: RestaurantType.IRestaurantInitial = {
	user: {
		name: "",
		username: "",
		history: [
			{
				restaurantName: "",
				date: "",
				time: "",
				people: 0,
			},
		],
	},
	restaurant: {
		name: "",
		totalQueue: 0,
		freeQueue: 0,
		image: [],
	},
	restaurantList: [],
};
export function RestaurantReducer(
	state = initialState,
	action: RestaurantAction.TRestaurantReduce
) {
	switch (action.type) {
		case "FETCH_RESTAURANT":
			return { ...state, restaurantList: action.payload };
		case "SEARCHING_NAME":
			// state.restaurantList = _.filter(state.restaurantList,elm=>elm.name.includes(action.payload))
			console.log("action", action.payload, state);
			return { ...state };
		default:
			return state;
	}
}
