			<TextDetail>
				<div>Time</div>
				<div style={{ width: "80%" }}>
					<DateTimePicker
						id="datetimepicker-1"
						onChange={(e) => {
							dispatch({
								type: "INPUT_DATETIME",
								payload: e,
							});
						}}

						// testId={"dateTimePicker"}
					/>
				</div>
			</TextDetail>

			<TextDetail className="mt-3">
				<button
					onClick={(e) =>
						dispatch({
							type: "MODAL_RESERVATION",
							payload: false,
						})
					}>
					Cancel
				</button>
				<button onClick={(e) => props.changePage("next")}>Reserve</button>
			</TextDetail>
		</>
	);
};
export default Reservation;
